#if you want a random board, remove the comments
import random
def random_board():
    init_grid = [[0 for i in range(grid_length)] for i in range(grid_height)]  # creates the grid of the game
    for i in range(grid_height):
        for j in range(grid_length):
            init_grid[i][j] = random.randint(0,1)
    return init_grid
current_grid = random_board()

def check_cell(y_axis,x_axis):
    """
    this function checks if the cell is valid
    :param y_axis: the y_axis of the cell
    :param x_axis: the x_axis of the cell
    :return: 0 or 1 if the cell is alive
    """
    if x_axis < 0 or x_axis == grid_length:
        return 0 #outofbounds
    return current_grid[y_axis][x_axis]

def check_neighbors_counter(y_axis,x_axis):
    """
    this function checks the horizntal cells of the cell/
    :param y_axis: the y_axis
    :param x_axis: the x_axis
    :return: amount of the the immident neighbors of the cell
    """

    counter = 0
    if y_axis < 0 or y_axis == grid_height:
        return 0 #outofbounds

    counter += check_cell(y_axis, x_axis -1)
    counter += check_cell(y_axis, x_axis)
    counter += check_cell(y_axis, x_axis + 1)
    return counter

def check_neighbors(y_axis,x_axis):
    """
    this function returns how many of the neighbors are alive
    :param y_axis: the number of the first list
    :param x_axis: the number of the column
    :return: int of the amount of live cells
    """
    alive_cells = 0
    alive_cells += check_neighbors_counter(y_axis-1,x_axis) #check the row below
    alive_cells += check_cell(y_axis,x_axis-1) #check the cell to the left
    alive_cells += check_cell(y_axis,x_axis+1) #check the cell to the right
    alive_cells += check_neighbors_counter(y_axis+1,x_axis) #check the row above
    return alive_cells

def tick(old_grid):
    current_grid = deep_copy(old_grid)
    """
    this function represents the tick of each movement
    grid is the unupdated grid
    and current_
    :return:
    """
    for tick_y in range(grid_height):
        for tick_x in range(grid_length):
            if old_grid[tick_y][tick_x] == 1: #the cell is alive
                if check_neighbors(tick_y,tick_x) < 2:
                    current_grid[tick_y][tick_x] = 0 #the cell die of underpopulation
                    draw_surface(False,tick_x,tick_y)
                elif check_neighbors(tick_y,tick_x) == (2 or 3):
                    pass #nothing happens
                elif check_neighbors(tick_y,tick_x) > 3:
                    current_grid[tick_y][tick_x] = 0
                    draw_surface(False,tick_x,tick_y)  # the cell die of overpopulation
            else: #the cell is dead
                if check_neighbors(tick_y,tick_x) == 3:
                    current_grid[tick_y][tick_x] = 1
                    draw_surface(True,tick_x,tick_y)
    return current_grid

def deep_copy(grid):
    """
    this is a function in order to make a seperation between the the two grids, the current and the updated.
    without it, the original grid will get updated when you update the the updated one and will cause problems.
    :param grid:
    :return: deep copy grid
    """
    return [[j for j in grid[i]] for i in range(grid_height)]

def print_grid(grid):
    """
    just for visualization in the consul
    :param grid:
    :return:
    """
    for lane in grid:
        print(lane)
    print("\n")

def draw_all_surface(grid):
    """
    draw the inital surface at the first time
    it can be used for each tick, but it is makes the game move very slow
    """
    screen.fill((0,0,0))
    for y_axis in range(grid_height): #location
        for x_axis in range(grid_length):
            if grid[y_axis][x_axis] == 1: #the cell is alive
                for i in range(pixel_size): #enlarge the pixel
                    for j in range(pixel_size):
                        screen.set_at((x_axis * pixel_size + i, y_axis * pixel_size + j), (255, 255, 255))

def draw_surface(alive,x_axis,y_axis):
    """
    update the current display
    the x_axis is the location in the x - coordination
    the y_axis is the location in the y - coordination
    we multiply the pixel size in order to get the coresponding location and the double loop to enlage the pixel

    """
    for i in range(pixel_size): #enlarge by pixel
         for j in range(pixel_size):
            if alive:
                screen.set_at((x_axis*pixel_size+i,y_axis*pixel_size+j), (255, 255, 255))
            else:
                screen.set_at((x_axis*pixel_size+i,y_axis*pixel_size+j), (0, 0, 0))


running = True
draw_all_surface(current_grid)
pygame.display.flip() #update the first time

while running:
    for event in pygame.event.get():
        if event.type == pygame.QUIT:
            running = False
    print_grid(current_grid) #for the consul
    current_grid = tick(current_grid) #update the board
    pygame.display.flip() #update the pygame
